mapper.insert:

	SELECT DASH_BOARD_SEQ.NEXTVAL FROM DUAL

/* bbs.dashBoard.insert */
INSERT INTO DASH_BOARD (
	BOARD_NO
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_WRITER
	, REGDATE
)VALUES(
	DASH_BOARD_SEQ.NEXTVAL
	, #{boardTitle}
	, #{boardContent}
	, #{boardWriter}
	, SYSTIMESTAMP
)

mapper.update:
/* bbs.dashBoard.update */
UPDATE DASH_BOARD
SET
	BOARD_TITLE = #{boardTitle}
	, BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

mapper.delete:
/* bbs.dashBoard.delete */
DELETE FROM DASH_BOARD
WHERE BOARD_NO = #{boardNo}

mapper.insert:
/* bbs.dashBoard.insertFile */
INSERT INTO DASH_BOARD_ATTCHFILE (
	FILE_NO
	, BOARD_NO
	, ORG_FILE_NAME
	, CHNG_FILE_NAME
	, FILE_SIZE
)VALUES(
	DASH_BOARD_ATTCHFILE_SEQ.NEXTVAL
	, #{boardNo}
	, #{orgFileName}
	, #{chngFileName}
	, #{fileSize}	
)
